      <p>{product && product.description}</p>
      <p>{product && product.price} â‚¬</p>
      {review &&
        review.map((review) => {
          return (
            <div>
              <h3>
                {review && review.title} - {review && review.updated_at} -{" "}
                {review && review.reviewUser.email}
